CREATE TABLE IF NOT EXISTS jogo_campeonato (id INTEGER PRIMARY KEY, id_campeonato INTEGER NOT NULL, time_1 TEXT NOT NULL, time_2 TEXT NOT NULL, time_1_gols INTEGER NOT NULL, time_2_gols INTEGER NOT NULL, time_1_logo TEXT NOT NULL, time_2_logo TEXT NOT NULL, campeonato INTEGER NOT NULL, liga TEXT NOT NULL, data INTEGER NOT NULL, campeonato_etapa TEXT NOT NULL, campeonato_etapa_rodada INTEGER NOT NULL);
CREATE TABLE IF NOT EXISTS artilheiro_jogo (id INTEGER PRIMARY KEY AUTOINCREMENT, nome TEXT NOT NULL, id_jogo INTEGER NOT NULL, _time TEXT NOT NULL, time_logo TEXT NOT NULL, gols INTEGER NOT NULL, liga TEXT NOT NULL);
CREATE TABLE IF NOT EXISTS artilheiro_campeonato (id INTEGER PRIMARY KEY AUTOINCREMENT, nome TEXT NOT NULL, id_campeonato INTEGER NOT NULL, _time TEXT NOT NULL, time_logo TEXT NOT NULL, gols INTEGER NOT NULL, liga TEXT NOT NULL);
CREATE TABLE IF NOT EXISTS cartao_jogo (id INTEGER PRIMARY KEY AUTOINCREMENT, id_jogo INTEGER NOT NULL, _time TEXT NOT NULL, time_logo TEXT NOT NULL, jogador TEXT NOT NULL, cartao TEXT NOT NULL);
CREATE TABLE IF NOT EXISTS suspensao_jogo (id INTEGER PRIMARY KEY AUTOINCREMENT, id_jogo INTEGER NOT NULL, _time TEXT NOT NULL, time_logo TEXT NOT NULL, jogador TEXT NOT NULL, criterio TEXT NOT NULL, motivo TEXT NOT NULL);
CREATE TABLE IF NOT EXISTS _time (id INTEGER PRIMARY KEY AUTOINCREMENT, nome TEXT NOT NULL, logo TEXT NOT NULL, pontos INTEGER NOT NULL, jogos INTEGER NOT NULL, saldo_gols INTEGER NOT NULL, liga TEXT NOT NULL, grupo TEXT NOT NULL);
